```{.r .cell-code}
x <- 1
total <- 0
sapply(dt, function(x) {
  total <- 0
  val <- x
  while (floor(val / 3) - 2 >= 0) {
    val <- floor(val / 3) - 2
    total <- total + val
  }
  return(total)
}) |>
  sum()
