  x <- str_replace_all(x, "\\(\\)|\\[\\]|\\{\\}|\\<\\>", "")
  if(nchar(x) == n) {
    t <- as.character(str_split(str_replace_all(x, "[\\(\\[\\{\\<]", ""),
                   "",
                   simplify = TRUE)[1,])
    t <- t[1]
    return(t)
  }
  return(monkeyBall(x, nchar(x)))
}

