::: {.cell hash='2021-10_cache/html/unnamed-chunk-3_b652a22e7ac2c8c4e1d7930ae8bdbd29'}

```{.r .cell-code}
dt2 <- dt

monkeyBall <- function(x, n) {
  x <- str_replace_all(x, "\\(\\)|\\[\\]|\\{\\}|\\<\\>", "")
  if(nchar(x) == n) {
    if(!grepl("\\)|\\]|\\}|\\>", x, perl = TRUE))
      return(x)
    return("")
  }
  return(monkeyBall(x, nchar(x)))
}

t <- c()
for(r in 1:nrow(dt2)) {
  x <- dt2[r,1]
  n <- nchar(x)
  t <- append(t, monkeyBall(x, n))
}
t <- t[nchar(t) > 0]
s <- c()
for(i in t) {
  miniS <- 0
  miniT <- rev(str_split(i, "", simplify = TRUE)[1,])
  for(l in miniT) {
    miniS <- (5 * miniS) + case_when(l == "(" ~ 1,
                                     l == "[" ~ 2,
                                     l == "{" ~ 3,
                                     l == "<" ~ 4)
  }
  s <- append(s, miniS)
}
s <- s[!is.na(s)]
median(s)
```

