find_biggest &lt;- function(x, value = integer(), pos = 0) {
  remaining &lt;- 12 - length(value)
  if (remaining == 0) return(value)
  biggest &lt;- which.max(x[(pos+1):(length(x)-remaining+1)]) + pos
  value &lt;- c(value, x[biggest])
  pos &lt;- biggest
  find_biggest(x, value, pos)
}

