file_name &lt;- "input.txt"
move &lt;- function(piles, i, ins, part) {
  moveN &lt;- ins$X2[i]
  fromN &lt;- ins$X3[i]
  toN &lt;- ins$X4[i]

  from &lt;- piles[[fromN]]
  to &lt;- piles[[toN]]

  if (part == 1) { to &lt;- c(rev(from[1:moveN]), to) }
  else if (part == 2) { to &lt;- c(from[1:moveN], to) }
  from &lt;- tail(from, -moveN)

  piles[[fromN]] &lt;- from[!is.na(from)]
  piles[[toN]] &lt;- to[!is.na(to)]

  return(piles)
}

do_part &lt;- function(part) {
