monkey_math &lt;- function(monkeys, op) {
  m1 &lt;- monkeys[[which(index == op[1])]]@val
  m2 &lt;- monkeys[[which(index == op[3])]]@val

  if (!is.na(m1) & !is.na(m2)) {
    if (op[2] == "+") { m1 + m2 }
    else if (op[2] == "-") { m1 - m2 }
    else if (op[2] == "*") { m1 * m2 }
    else if (op[2] == "/") { m1 / m2 }
  } else { return(NA_integer_) }
