replace_monkey <- function(m) {
  w <- which(index == m)
  if (length(w) == 0) { return(m) }
  v <- monkeys[[w]]@val
  if (is.na(v)) { return(paste0("( ", monkeys[[which(index == m)]]@op, " )")) }
  return(paste0("( ", v, " )"))
