check_consecutive <- function(x) {
  x <- paste0(x, collapse = "")
  letters |>
    runner(k = 3, f = paste0, collapse = "") |>
    tail(-2) |>
    map_lgl(~ str_detect(x, .x)) |>
    any()
}

check_confusing <- function(x) { !any(c("i", "o", "l") %in% x) }

check_pairs <- function(x) {
  x <- 
    data.frame(
      "a" = x,
      "b" = lead(x)
    ) |>
    filter(a == b) |>
    group_by(a, b) |>
    count() |>
    nrow()
  return(x >= 2)
}

check <- function(x) {
  return(
    check_consecutive(x) & 
    check_confusing(x) &
    check_pairs(x)
  )
}

no_iol <- letters[!(letters %in% c("i", "o", "l"))]

replace <- function(x, pos) {
  l <- which(no_iol == x[pos]) + 1
  if (l > length(no_iol)) {
    x[pos] <- "a"
    x <- replace(x, pos - 1)
  } else {
    x[pos] <- no_iol[l]
  }
  return(x)
}

