target_pos &lt;- integer(length(end))
repeat {
  end &lt;- start(end, dir[(count %% length(dir)) + 1])
  if (any(end %in% targets)) {
    target_pos[end %in% targets] &lt;- count
    if (!any(target_pos == 0)) break
