    x &lt;- as.integer(x) + 1
    y &lt;- as.integer(y) + 1
    pos &lt;- sapply(letters[1:16], \(r) self[[r]])
    x &lt;- names(which(pos == x))
    y &lt;- names(which(pos == y))
    self$partner(x, y)
  },
  "partner" = \(x, y) {
    tmp &lt;- self[[y]]
    self[[y]] &lt;- self[[x]]
    self[[x]] &lt;- tmp
  }
