parse_input &lt;- function(x) {
  y &lt;-
    str_match(x, "(\\d+)(x| by )(\\d+)")[1, c(2, 4)] |&gt;
    as.numeric()
  a &lt;- y[1]; b &lt;- y[2];
  if (str_detect(x, "rect")) {
    x &lt;- \(m) { m[1:b, 1:a] &lt;- "#"; m; }
  } else {
    a &lt;- a + 1
    if (str_detect(x, "rotate row")) {
      x &lt;- \(m) { m[a,] &lt;- cycle(m[a,], b); m; }
    } else if (str_detect(x, "rotate col")) {
      x &lt;- \(m) { m[,a] &lt;- cycle(m[,a], b); m; }
    }
  }
  return(x)
}

