check_consecutive &lt;- function(x) {
  x &lt;- paste0(x, collapse = "")
  letters |&gt;
    runner(k = 3, f = paste0, collapse = "") |&gt;
    tail(-2) |&gt;
    map_lgl(~ str_detect(x, .x)) |&gt;
    any()
}

check_confusing &lt;- function(x) { !any(c("i", "o", "l") %in% x) }

check_pairs &lt;- function(x) {
  x &lt;- 
    data.frame(
      "a" = x,
      "b" = lead(x)
    ) |&gt;
    filter(a == b) |&gt;
    group_by(a, b) |&gt;
    count() |&gt;
    nrow()
  return(x &gt;= 2)
}

check &lt;- function(x) {
  return(
    check_consecutive(x) & 
    check_confusing(x) &
    check_pairs(x)
  )
}

no_iol &lt;- letters[!(letters %in% c("i", "o", "l"))]

replace &lt;- function(x, pos) {
  l &lt;- which(no_iol == x[pos]) + 1
  if (l &gt; length(no_iol)) {
    x[pos] &lt;- "a"
    x &lt;- replace(x, pos - 1)
  } else {
    x[pos] &lt;- no_iol[l]
  }
  return(x)
}

